if_conditional.code:
if condition then
    statements;
end if;

if_else_conditional.code:
if condition then
    statements;
else
    statements;
end if;

if_elseif_conditional.code:
if condition then
    statements;
elsif condition then
    statements;
end if;

if_elseif_else_conditional.code:
if condition then
    statements;
elsif condition then
    statements;
else
    statements;
end if;

ternary_conditional.code:
Variable := (if condition
    then expression
    else expression);

while_loop.code:
while condition loop
    statements;
end loop;

do_while_loop.code:
loop
    statements;
    exit when not condition;
end loop;

for_loop.code:
for I in First_Index .. Last_Index loop
    statements;
end loop;
