if_conditional.code:
if (condition) {
    code block to be executed
}

if_else_conditional.code:
if (condition) {
    code block to be executed
} else {
    code block to be executed
}

if_elseif_conditional.code:
if (condition) {
    code block to be executed
} else if (condition) {
    code block to be executed
}

if_elseif_else_conditional.code:
if (condition) {
    code block to be executed
} else if (condition) {
    code block to be executed
} else {
    code block to be executed
}

switch_statement.code:
switch (expression) {
  case x:
    statements;
    break;
  case y:
    statements;
    break;
  default:
    statements;
}

while_loop.code:
while (condition) {
    code block to be executed
}

do_while_loop.code:
do {
    code block to be executed
} while (condition);

for_loop.code:
for (initialization_statement; condition; update_statement){
    code block to be executed
}
