if_conditional.code:
if (condition) {
    code block to be executed
}

if_else_conditional.code:
if (condition) {
    code block to be executed
} else {
    code block to be executed
}

if_elseif_conditional.code:
if (condition) {
    code block to be executed
} else if (condition) {
    code block to be executed
}

if_elseif_else_conditional.code:
if (condition) {
    code block to be executed
} else if (condition) {
    code block to be executed
} else {
    code block to be executed
}

while_loop.code:
while (condition) {
    code block to be executed
}

do_while_loop.code:
do {
    code block to be executed
} while (condition);

for_loop.code:
for (statement1 /*counter variable(s)*/; statement2 /*condition to keep the loop running*/; statement3 /*statement executed each time after the loop has been executed*/){
    code block to be executed
}
